mapper.select:
SELECT a.id, a.title, a.publishDate, a.editTime, a.author, a.conference, a.link, a.type, u.nickname, d.directionName FROM article a, user u, search_direction d WHERE a.uid=u.id AND a.direction=d.ID

    AND state=#{state}


    AND uid=#{uid} AND state=1


    AND title LIKE concat('%',#{keywords},'%')


    AND u.nickname=#{nickname}


    AND a.author LIKE concat('%',#{author},'%')


    AND a.type=#{type}


    AND a.conference LIKE concat('%',#{conference},'%')


    AND d.directionName=#{direction}

ORDER BY editTime DESC limit #{start},#{count};

SELECT a.id,a.`title`,a.`editTime`,a.`pageView`,a.`state`,u.`nickname`,c.`cateName`,a.uid FROM article a,user u,category c WHERE a.`cid`=c.`id` AND a.`uid`=u.`id` and a.state=1
AND title LIKE concat('%',#{keywords},'%')
ORDER BY a.editTime DESC limit #{start},#{count};

SELECT count(*) FROM article a, user u, search_direction d WHERE a.uid=u.id AND a.direction=d.ID

    AND state=#{state}


    AND uid=#{uid} AND state=1


    AND a.title LIKE concat('%',#{keywords},'%')


    AND u.nickname=#{nickname}


    AND a.author LIKE concat('%',#{author},'%')


    AND a.type=#{type}


    AND a.conference LIKE concat('%',#{conference},'%')


    AND d.directionName=#{direction}
